%% define proble%% min c'x s.t. ax = b, x>= 0%% max b'v s.t. a'v + s = c, s>=0a=[-0.973528, 0.0919762, 0.843787; -0.52274, 0.686307, -0.762521];b=[0.758965; 0.648628];c=[-0.732288; 1.06376; -0.742943];tx = -0.2:0.05:0.8;ty = 0.8:0.05:2;[vx, vy] = meshgrid(-0.2:0.05:0.8, 0.8:0.05:2);[nx, ny] = size(vx);f = zeros(nx, ny);%% misfit functionfor ix = 1 : nx   for iy = 1 : ny     v = [vx(ix, iy); vy(ix, iy)];    f(ix, iy) = b' * v;  end end;%% boundaryvv = [-0.2 : 0.1 : 0.8]';nv = length(vv);fb = zeros(nv, 3);fc = zeros(nv, 3);for k = 1 : 3   fb(:, k) = (c(k) - a(1, k) * vv) / a(2, k);   fc(:, k) = b(1) * vv + b(2) * fb(:, k);end ;%% inner pointsg = ones(nx, ny);gt = zeros(3, 1);for ix = 1 : nx   for iy = 1 : ny     for k = 1 : 3      gt(k) = a(1, k) * vx(ix, iy) + a(2, k) * vy(ix, iy) - c(k);      if (gt(k) > 0)         g(ix, iy) = 0;      end            end    end end;%% read inverted resultsol = load('output/kktlp_matrix_test.txt');xi = sol(:, 1:3);vi = sol(:, 4:5);si = sol(:, 6:8);for k = 1 : size(vi, 1)  for i = 1 : 3      res = a(1, i) * vi(k, 1) + a(2, i) * vi(k, 2) - c(i);      disp([k, i, res, si(i)])  end end ;is_feasible(xi(1, :)', vi(1, :)', si(1, :)', a, b, c)figure(1)mesh(vx, vy, f);hold off;figure(2)imagesc(tx, ty, f);colorbarhold on;#contour(tx, ty, f);#hold on;plot(vx(g>0), vy(g>0), 'or', 'MarkerFaceColor', 'r');hold on plot(vv, fb, 'k', 'LineWidth', 2);hold on plot(vi(:,1), vi(:,2), '^-k');xlim([-0.2 0.8])ylim([0.8 2])set(gca, 'YDir', 'Normal');hold off;